import { useState } from "react";
import * as BooksAPI from '../BooksAPI';

const Book = () => {

    const [bookList, setBookList] = useState([]);

    const loadBooks = () =>{
        bookList = BooksAPI.getAll();
    }

    loadBooks();

    return (
        <div className = "books-grid" key = "index">
            {bookList.map(book => <div className="books-grid li">
                {book}
            </div>
                
            )}
        </div>
    );
}

export default Book;
