import { useState, useEffect } from "react";
import * as BooksAPI from '../BooksAPI';

const Book = () => {

    const [bookList, setBookList] = useState([]);

    useEffect( () => {

        const loadBooks = async ()  =>{
            const res = await BooksAPI.getAll();
     
             setBookList(res);
             //console.log(bookList);
         };

         loadBooks(); 
    }, []);
    

    

    return (
        <div className = "books-grid">
            {bookList}
        </div>
    );
}

export default Book;
